"use strict";function showpassword(e,t){let s=document.getElementById(e),a=document.getElementById(t);"password"==s.type?(s.type="text",a.className="fas fa-eye"):(s.type="password",a.className="fas fa-eye-slash")}window.onload=function(){const e=document.querySelectorAll(".needs-validation");Array.prototype.slice.call(e).forEach(function(e){e.addEventListener("submit",function(t){e.checkValidity()||(t.preventDefault(),t.stopPropagation()),e.classList.add("was-validated")},!1);e.querySelectorAll("input, textarea, select").forEach(function(e){const t=()=>{e.checkValidity()?(e.classList.remove("is-invalid"),e.classList.add("is-valid")):(e.classList.remove("is-valid"),e.classList.add("is-invalid"))};e.addEventListener("input",t),e.addEventListener("blur",t)})})};